import { Link } from "react-router-dom";
import CategoryNavigator from "./CategoryNavigator";
import axios from "axios";


const ProductCard = (product) => {

  const admin = JSON.parse(sessionStorage.getItem("active-admin"));

  const deleteProduct = () => {
    const formData = new FormData();
    formData.append("id", product.item.id);

    axios
      .post("http://localhost:8080/api/product/delete", formData)
      .then((resp) => {
        let result = resp.data.data;
        alert("Product saved successfully");
      })
      .catch((error) => {
        console.log("Error", error);
        alert("Error saving product");
      });
  };

  return (
    <div className="col">
    <div class="card border-color rounded-card card-hover product-card custom-bg h-100">
      <img
        src={"http://localhost:8080/api/product/" + product.item.imageName}
        class="card-img-top rounded mx-auto d-block m-2"
        alt="img"
        style={{
          maxHeight: "270px",
          maxWidth: "100%",
          width: "auto",
        }}
      />

      <div class="card-body text-color">
        <h5 class="card-title d-flex justify-content-between">
          <div>
            <b>{product.item.title}</b>
          </div>
          <CategoryNavigator
            item={{
              id: product.item.category.id,
              title: product.item.category.title,
            }}
          />
        </h5>
        <p className="card-text">
          <b>{product.item.description}</b>
        </p>
      </div>
      <div class="card-footer">
        <div className="text-center text-color">
          <p>
            <span>
              <h4>Price : &#8377;{product.item.price}</h4>
            </span>
          </p>
        </div>
        <div className="d-flex justify-content-between">
          <Link
            to={`/product/${product.item.id}/category/${product.item.category.id}`}
            className="btn bg-color custom-bg-text"
          >
            Add to Cart
          </Link>
          {admin && <form
            action={`/product/${product.item.id}/category/${product.item.category.id}`}
            className="btn bg-color custom-bg-text"
          >
            Delete
          </form>}

          <p class="text-color">
            <b>
              <i>Stock :</i> {product.item.quantity}
            </b>
          </p>
        </div>
      </div>
    </div>
    </div>
  );
};

export default ProductCard;
